"use strict";

//Utilizando o for
var numero = 3;

for (var i = 1; i <= 10; i++) {
  console.log("".concat(i, " x ").concat(numero, " = ").concat(i * numero));
} //Decremento


for (var a = 10; a >= 0; a--) {
  console.log(a);
} //Outro exemplo


for (var b = 20; b >= 0; b--) {
  console.log(b + b);
}